package org.ip.sesion01;

public class ConvertirCelsiusFahrenheit {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//3. Haz un programa que convierta grados centígrados a Fahrenheit.
		

	double centigrados=31.00f;
	double Fahrenheit=9*31/5 + 32;
		
	System.out.printf("Los grados Fahrenheit correspondientes a %2.2f",centigrados); 
	System.out.printf(" grados Celsius son => %2.2f",Fahrenheit);			
		
	}

}
